import React from 'react';
import '../App.css';
import { Flex, Box } from 'rebass'

const gameStyle = {
  align: 'center',
  width: '100%'
}

const dealerStyle = {
  height: '250px',
  background: 'blue',
  width: '100%',
}

const playerStyle = {
  display: 'flex',
  width: '50%',
}

const aiStyle = {
  display: 'flex',
  width: '50%',
}

const BlackjackGame = () => {
  return (
    <Box style={gameStyle}>
      <Box style={dealerStyle}>
        Dealer
      </Box>
      <Flex style={playerStyle}>
        Player
        <button>HIT</button>
        <button>STAY</button>
        <button>PLAY AGAIN</button>
      </Flex>
      <Flex style={aiStyle}>
        AI
      </Flex>
    </Box>
)
}

export default BlackjackGame;
