import React from 'react';
import '../App.css';
import { Flex, Box, Image } from 'rebass'
import blackjackImage from '../images/BlackjackImage.png'

const gameStyle = {
  align: 'center',
  width: '100%'
}

const dealerStyle = {
  height: '250px',
  background: 'blue',
  width: '100%',
}

const playersStyle = {
  height: '500px',
}

const playerStyle = {
  display: 'block',
  width: '50%',
  background: 'red',

}

const aiStyle = {
  display: 'flex',
  width: '50%',
  background: 'gray',
}

const cardsDisplayStyle = {
  width: '100%'
}

const gameButtons = {
  margin: 'auto',
  width: '100%',
  align: 'center',
}

const gameButton = {
  textAlign: 'center',
  height: '10%',
  width: '50%'
}

const BlackjackGame = () => {
  return (
    <Box style={gameStyle}>
      <Box style={dealerStyle}>
        Dealer
      </Box>
      <Flex style={playersStyle}>
        <div style={playerStyle}>
          <Flex style={cardsDisplayStyle}>
            <Image src={blackjackImage}/>
            <Image src={blackjackImage}/>
            <Image src={blackjackImage}/>
          </Flex>
          <div style={gameButtons}>
            <button style={gameButton}>HIT</button>
            <button style={gameButton}>STAY</button>
            <button style={gameButton}>PLAY AGAIN</button>
          </div>
        </div>
        <Box style={aiStyle}>
          AI
        </Box>
      </Flex>
    </Box>
)
}

export default BlackjackGame;
