import React from 'react';

//https://github.com/BrettSchlereth/CharterHW/blob/main/transactionSQL.sqlite?raw=true

const initSqlJs = require('sql.js');


class CharterProject extends React.Component {

  constructor(props) {
    super(props);
    this.loadDataset()
  }

  async loadDataset () {

    // const SQL = await initSqlJs({
    //   // Required to load the wasm binary asynchronously. Of course, you can host it wherever you want
    //   // You can omit locateFile completely when running in node
    //   locateFile: file => `https://sql.js.org/dist/${file}`
    // })

    // const sqlPromise = initSqlJs({
    // locateFile: file => `transactionSQL.sqlite`
    // });
    // const dataPromise = fetch("transactionSQL.sqlite").then(res => res.arrayBuffer());
    // const [SQL, buf] = await Promise.all([sqlPromise, dataPromise])
    // const db = new SQL.Database(new Uint8Array(buf));
    // console.log("db: ", db)

    const xhr = new XMLHttpRequest();
    // For example: https://github.com/lerocha/chinook-database/raw/master/ChinookDatabase/DataSources/Chinook_Sqlite.sqlite
    xhr.open('GET', 'https://github.com/BrettSchlereth/CharterHW/blob/main/transactionSQL.sqlite?raw=true', true);
    xhr.responseType = 'arraybuffer';

    xhr.onload = e => {
      const uInt8Array = new Uint8Array(xhr.response);
      const db = new SQL.Database(uInt8Array);
      const contents = db.exec("SELECT * FROM my_table");
      // contents is now [{columns:['col1','col2',...], values:[[first row], [second row], ...]}]
    };
    xhr.send();
  }


  render() {
    return(
      <div>
        returned from script again
      </div>)
  }
}

export default CharterProject;
