import React from 'react';
import '../App.css';
import { Flex, Box, Image } from 'rebass'
import blackjackImage from '../images/BlackjackImage.png'

const gameStyle = {
  background: 'brown',
  align: 'center',
  height: '100%',
  width: '100%',
  display: 'flex'
}

const playerSideStyle = {
  width: '50%',
}

const aiSideStyle = {
  width: '50%',
}

const dealerStyle = {
  height: '250px',
  background: 'blue',
}

const playersStyle = {
  height: '500px',
}

const playerStyle = {
  display: 'block',
  background: 'red',

}

const aiStyle = {
  display: 'flex',
  background: 'gray',
}

const cardsDisplayStyle = {
  width: '100%',
}

const cardStyle = {
  maxHeight: '1%',
}

const gameButtons = {
  margin: 'auto',
  width: '100%',
  align: 'center',
}

const gameButton = {
  textAlign: 'center',
  height: '10%',
  width: '50%'
}

const BlackjackGame = () => {
  return (
    <Box style={gameStyle}>
      <Box style={playerSideStyle}>
        <Box style={dealerStyle}>
          Dealer
        </Box>
        <div style={playerStyle}>
          <Flex style={cardsDisplayStyle}>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
          </Flex>
          <div style={gameButtons}>
            <button style={gameButton}>HIT</button>
            <button style={gameButton}>STAY</button>
            <button style={gameButton}>PLAY AGAIN</button>
          </div>
        </div>
      </Box>
      <Box style={aiSideStyle}>
        <Box style={dealerStyle}>
          AI Dealer
        </Box>
        <div style={playerStyle}>
          <Flex style={cardsDisplayStyle}>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
            <Image style={cardStyle} src={blackjackImage}/>
          </Flex>
        </div>
      </Box>
    </Box>
)
}

export default BlackjackGame;
