import React from 'react';
import '../App.css';
import { Flex, Box } from 'rebass'

const gameStyle = {
  align: 'center',
  width: '100%'
}

const dealerStyle = {
  height: '250px',
  background: 'blue',
  width: '100%',
}

const playersStyle = {
  height: '25%',
}

const playerStyle = {
  display: 'flex',
  height: '25%',
  width: '50%',
  background: 'red',

}

const aiStyle = {
  display: 'flex',
  height: '25%',
  width: '50%',
  background: 'gray',
}

const BlackjackGame = () => {
  return (
    <Box style={gameStyle}>
      <Box style={dealerStyle}>
        Dealer
      </Box>
      <Flex style={playersStyle}>
        <Box style={playerStyle}>
          Player
          <button>HIT</button>
          <button>STAY</button>
          <button>PLAY AGAIN</button>
        </Box>
        <Box style={aiStyle}>
          AI
        </Box>
      </Flex>
    </Box>
)
}

export default BlackjackGame;
