import React from 'react';
import { useState, setState } from 'react';
import '../App.css';
import { Box, Image } from 'rebass'
import CardsDisplay from './CardsDisplay.js'
import twoOfSpades from '../images/2ofSpades.png'
import threeOfSpades from '../images/3ofSpades.png'
import fourOfSpades from '../images/4ofSpades.png'
import fiveOfSpades from '../images/5ofSpades.png'
import sixOfSpades from '../images/6ofSpades.png'
import sevenOfSpades from '../images/7ofSpades.png'
import eightOfSpades from '../images/8ofSpades.png'
import nineOfSpades from '../images/9ofSpades.png'
import tenOfSpades from '../images/10ofSpades.png'
import aceOfSpades from '../images/aceOfSpades.png'

const cardStyle = {
  height: 'auto',
  maxWidth: '20%',
}

const cardValues = {
  2: twoOfSpades,
  3: threeOfSpades,
  4: fourOfSpades,
  5: fiveOfSpades,
  6: sixOfSpades,
  7: sevenOfSpades,
  8: eightOfSpades,
  9: nineOfSpades,
  10: tenOfSpades,
  11: aceOfSpades
}

class Hand {
  constructor(handOwner) {
    this.owner = handOwner;
    this.cards = [];
    this.soft = 0;
    this.blackjack = false;
    this.count = 0;
    this.total = 0;
  }

  addCard(deck) {
    this.cards.push(deck.pop())
  }

  getTotalCardValue() {
    var total = 0
    this.cards.forEach(card => total += card);
    this.total = total
    return total;
  }
}

const gameStyle = {
  background: 'brown',
  align: 'center',
  display: 'flex'
}

const playerSideStyle = {
  width: '50%',
}

const aiSideStyle = {
  height: '100%',
  width: '50%',
}

const dealerStyle = {
  height: 'auto',
  maxHeight: '25em',
  background: 'blue',
}

const playerStyle = {
  background: 'green',
}

const aiStyle = {
  background: 'gray',
}

const gameButtons = {
  margin: 'auto',
  width: '100%',
  align: 'center',
}

const gameButton = {
  textAlign: 'center',
  height: '10%',
  width: '50%'
}

var deck;
var aiDeck;
var dealerHand = new Hand("dealer");
var playerHand = new Hand("player");
var aiDealerHand = new Hand("dealer");
var aiPlayerHand = new Hand("player");

class BlackjackGame extends React.Component {
  constructor(props) {
    super(props);
    this.startGame();
    this.state = {
      playerHand : [],
      aiHand: [],
      hitValue: false,
      stayValue: false,
      restartValue: false
    }
  }

  startGame() {
    this.setState({
      playerHand: []

    })
    buildDeck()
    dealCards()
    if (checkForBlackjack(dealerHand, playerHand) == true) {
      console.log("blackjack!")
      //disable hit and stay
    }
    else {
      //get player moves until stay or bust
      //get dealer moves
      //let the ai play
      //let the ai's dealer play
      //add scores
      //clear on restart
    }

    console.log('start')
  }



  hit(playerType) {
   switch (playerType) {
     case "player":
       this.setState({
         playerHand: playerHand.addCard(deck)
       })
       playerHand.getTotalCardValue()
        break;
     case "dealer":
        console.log("dealer hit")
        break;
     case "aiPlayer":
        break;
     case "aiDealer":
        break;
  }

  }

  stay() {

  }

  render() {
    return (
      <Box style={gameStyle}>
        <Box style={playerSideStyle}>
          <Box style={dealerStyle}>
            Dealer
            {getCards(dealerHand, "dealer")}
          </Box>
        <Box style={playerStyle}>
          Player
          {getCards(playerHand, "player")}
          <div style={gameButtons}>
            <button disabled={!this.state.hitValue} onClick={() => this.hit("player")} style={gameButton}>HIT</button>
            <button id="stay" onClick={() => this.stay()} style={gameButton}>STAY</button>
            <button id="restart" onClick={() => this.startGame()} style={gameButton}>PLAY AGAIN</button>
          </div>
          </Box>
        </Box>
        <Box style={aiSideStyle}>
          <Box style={dealerStyle}>
            AI Dealer
            {getCards(aiDealerHand, "dealer")}
          </Box>
          <Box style={aiStyle}>
            AI Player
            {getCards(aiPlayerHand, "aiPlayer")}
          </Box>
        </Box>
      </Box>
    )
  }
}

function buildDeck() {
  deck = []
  for (var i = 0; i < 8; i++) {
    for (var j = 2; j < 12; j++) {
      if (j === 10) {
        deck.push(j)
        deck.push(j)
        deck.push(j)
      }
      deck.push(j)
    }
  }
  shuffle(deck)
  aiDeck = deck
  console.log("building deck")
}

function checkForBlackjack(dealer, player) {
  if (dealer.total != 21 && player.total != 21) {
    return false
  }
  console.log("blackjack!")
  return true
}

function dealCards() {
  playerHand.cards = [];
  dealerHand.cards = [];
  aiPlayerHand.cards = [];
  aiDealerHand.cards = [];
  for (var i = 0; i < 2; i++) {
    playerHand.addCard(deck)
    dealerHand.addCard(deck)
  }
  aiPlayerHand.cards = playerHand.cards.slice()
  aiDealerHand.cards = dealerHand.cards.slice()
  console.log("dealing cards")
}

function getCard(card, index) {
  return (
    <Image key={index.toString()} style={cardStyle} src={cardValues[card]}/>
  );
}

function getCards(hand, playerType) {
  var cards = []
  for (var i=0; i<hand.cards.length; i++) {
      var card = hand.cards[i]
      cards.push(getCard(card, i))
      if (playerType === "dealer") {
        i = hand.cards.length
      }
  }
  return cards;
}

function restart() {

}

function shuffle(array) {
  var currentIndex = array.length,  randomIndex;

  // While there remain elements to shuffle...
  while (0 !== currentIndex) {

    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }

  return array;
}







// function getRandomIntInclusive(min, max) {
//   min = Math.ceil(min);
//   max = Math.floor(max);
//   return Math.floor(Math.random() * (max - min + 1) + min); //The maximum is inclusive and the minimum is inclusive
// }


export default BlackjackGame;
