import React from 'react'
import props from 'prop-types'
import { Box, Card, Image, Heading, Text, Link, Flex } from 'rebass'
import { NavLink, Switch, Route } from 'react-router-dom'
import bunkerDownImage from '../images/600x900BunkerDown.png'
import dInfinityImage from '../images/MortyArchmage.png'
import Boop from './Boop.js'

var projects = {"Bunker Down": {"type":"Video Game", "image":bunkerDownImage, "Link": '/BunkerDown'},
                "DInfinity": {"type":"Video Game", "image":dInfinityImage, "Link": '/DInfinity'},
                "Resume": {"type":"Master Resume", "image":dInfinityImage, "Link": '/Resume'}
              }

const ProjectCards = () => {
  var cards = []
  if (cards.length == 0) {
    for (var project in projects) {
      cards.push(getProjectCard(project))
    }
  }
  return (
    <Flex flexWrap='wrap' display='block' align="center">
    {cards}
    </Flex>
  )
};

const cardStyle = {
  alignItems:'center',
  p:1,
  borderRadius: 10,
  background: '#1F2833',
  boxShadow: '0 0 16px rgba(0, 0, 0, .25)'
}

const imageStyle = {
  height:"300px",
  display:"block",
  margin:"auto"
}

const nameStyle = {
  fontFamily:"Arial",
  color:"#66FCF1"
}

const typeStyle = {
  textAlign:"center",
  color:"#66FCF1",
  fontSize: 15
}

function getProjectCard(project) {
  return (
    <Boop moveAmount={-10} timing={200}>
      <Box margin='10px'>
        <NavLink exact activeClassName="current" to={projects[project]["Link"]}>
          <Box width={256} mx='auto'>
            <Card mx='auto' style={cardStyle}>
                <Image src={projects[project]["image"]} style={imageStyle}/>
              <Box px={2}>
                <Heading style={nameStyle}>
                  {project}
                </Heading>
                <Text style={typeStyle}>
                  {projects[project]["type"]}
                </Text>
              </Box>
            </Card>
          </Box>
        </NavLink>
      </Box>
    </Boop>
  );
}

export default ProjectCards;
