import React from 'react';
import initSqlJs from 'sql.js';


//https://github.com/BrettSchlereth/CharterHW/blob/main/transactionSQL.sqlite?raw=true



var database

class CharterProject extends React.Component {

  constructor(props) {
    super(props);
    this.loadDataset()
    this.state={
      database: null,
      SQLresponse: null
    }

  }

  getQuery(query) {
    return this.state.database.exec(query)[0]["values"]
  }

  async loadDataset () {
    const sqlPromise = initSqlJs({locateFile: file => `sql-wasm.wasm`});
    const dataPromise = fetch("transactionSQL.sqlite").then(res => res.arrayBuffer());
    try {
      const [SQL, buf] = await Promise.all([sqlPromise, dataPromise])
        var db = new SQL.Database(new Uint8Array(buf));
        this.setState({
          database: db
        })
    }
    catch (err) {
      console.log(err);
      console.log("Failed to load database")
    }
    console.log("db: ", this.state.database)
  }

  showSomething() {
    console.log("response: ", this.state.database.exec("SELECT * FROM transactionData WHERE TransactionId=4"))
    this.setState({
      SQLresponse: this.getQuery("SELECT * FROM transactionData WHERE TransactionID=John")
    })
  }
