import nineOfSpades from '../images/9ofSpades.png'
import tenOfSpades from '../images/10ofSpades.png'
import aceOfSpades from '../images/aceOfSpades.png'
import * as tf from '@tensorflow/tfjs'

const cardStyle = {
  height: 'auto',
  maxWidth: '20%',
}

const cardValues = {
  2: twoOfSpades,
  3: threeOfSpades,
  4: fourOfSpades,
  5: fiveOfSpades,
  6: sixOfSpades,
  7: sevenOfSpades,
  8: eightOfSpades,
  9: nineOfSpades,
  10: tenOfSpades,
  11: aceOfSpades
}

class Hand {
  constructor(handOwner) {
    this.owner = handOwner;
    this.cards = [];
    this.soft = 0;
    this.blackjack = false;
    this.count = 0;
    this.total = this.getTotalCardValue();
    this.busted = false;
  }

  addCard(deck) {
    this.cards.push(deck.pop())
    this.getTotalCardValue()
  }

  getTotalCardValue() {
    var total = 0
    var handCount = 0
    this.cards.forEach(card => {
      if (card <= 6) {
        handCount += 1
      }
      else if (card >= 10) {
        handCount -= 1
      }
      this.count = handCount
      if (card === 11 && total + card > 21) {
        card = 1
      }
      else if (card === 11 && this.soft === 0) {
        this.soft = 1
      }
      else if (total + card > 21 && this.soft === 1) {
        total -= 10
        this.soft = 0
      }
      total += card
      this.total = total
    });

    this.total = total
    if (total > 21) {
      this.busted = true;
    }
    else {
      this.busted = false;
    }
    return total;
  }
}

const gameStyle = {
  background: 'brown',
  align: 'center',
  display: 'flex'
}

const playerSideStyle = {
  width: '50%',
}

const aiSideStyle = {
  height: '100%',
  width: '50%',
}

const dealerStyle = {
  height: 'auto',
  maxHeight: '25em',
  background: 'blue',
}

const playerStyle = {
  background: 'green',
}

const aiStyle = {
  background: 'gray',
}

const gameButtons = {
  margin: 'auto',
  width: '100%',
  align: 'center',
}

const gameButton = {
  textAlign: 'center',
  height: '10%',
  width: '50%'
}

var deck;
var aiDeck;
var dealerHand = new Hand("dealer");
var playerHand = new Hand("player");
var aiDealerHand = new Hand("dealer");
var aiPlayerHand = new Hand("player");
var model

class BlackjackGame extends React.Component {
  constructor(props) {
    super(props);
    model = this.loadTheModel()
    .then(function(result) {
      return result
    })
    .catch(console.log("failure"))
    console.log("model: ", model)
    this.startGame();
    this.state = {
      playerHand : [],
      dealerHand: [],
      aiHand: [],
      aiDealerHand: [],
      hitDisabled: false,
      stayDisabled: false,
      restartDisabled: true,
      playerDone: false,
      dealerDone: false,
      aiPlayerDone: false,
      message: "hit or stay",
      aiMessage: "",
    }

  }

  checkForBlackjack(dealer, player) {
    if (dealer.total === 21 && player.total === 21) {
      this.setState({
        message: "Push!",
      });
      return true;
    }
    else if (dealer.total === 21) {
      this.setState({
        message: "Dealer has blackjack!"
      })
      return true
    }
    else if (player.total === 21) {
      this.setState({
        message: "Player has blackjack!"
      })
      return true
    }
    else return false
    }

  async getAiDealerMoves() {

  }

  async getAiMoves() {
    while (aiPlayerHand.busted === false) {
      var predictions = await this.getPrediction()
      console.log("predictions: ", predictions)
      if (predictions[0] >= predictions[1] && predictions[0] > 0.5) {
        console.log("ai is hitting")
        this.hit("aiPlayer")
      }
      else {
        console.log("ai is holding")
        break
      }
    }
    this.getDealerMoves(aiDealerHand, aiPlayerHand)
  }

  getCards(hand, playerType) {
    var cards = []
    for (var i=0; i<hand.cards.length; i++) {
        var card = hand.cards[i]
        cards.push(getCard(card, i))
        if (playerType === "dealer" & this.state.playerDone === false) {
          i = hand.cards.length
        }
    }
    return cards;
  }

  getCount(hand1, hand2) {
    return hand1.count + hand2.count
  }

  getDealerMoves(dealer, player) {
    console.log("starting dealer moves")
    console.log("dealer total", dealer.getTotalCardValue())
    while (dealer.total < 17) {
      if (dealer.owner === "dealer") {
        this.hit("dealer")
      }
      else {
        this.hit("aiDealer")
      }
    }
    this.getWinner(dealer, player)
    this.getAiMoves()
    this.setState({
      restartDisabled: false
    })
  }

  async getPrediction() {
    model = await tf.loadLayersModel('model.json');
    console.log("dealerHand.total", aiDealerHand.cards[0])
    var hitPredictionInput = tf.tensor([[aiDealerHand.cards[0], aiPlayerHand.getTotalCardValue(), this.getCount(aiDealerHand, aiPlayerHand), aiPlayerHand.soft, 1]])
    var stayPredictionInput = tf.tensor([[aiDealerHand.cards[0], aiPlayerHand.getTotalCardValue(), this.getCount(aiDealerHand, aiPlayerHand), aiPlayerHand.soft, 0]])
    var hitPrediction = model.predict(hitPredictionInput).dataSync()[0]
    var stayPrediction = model.predict(stayPredictionInput).dataSync()[0]
    console.log("hit: ", hitPrediction, " stay: ", stayPrediction)
    return [hitPrediction, stayPrediction]
    }

  getWinner(dealer, player) {
    var winnerMessage = ""
    console.log("dealerTotal:", dealer.total)
    if (dealer.busted) {
      winnerMessage = "Dealer Busted! Player Wins!"
    }
    else if (dealer.total > player.total) {
      winnerMessage = "Dealer Wins!"
    }
    else if (player.total > dealer.total) {
      winnerMessage = "Player Wins!"
    }
    else {winnerMessage = "Push!"}
    this.setState({
      message: winnerMessage
    })
  }

  hit(handOwner) {
   switch (handOwner) {
     case "player":
       this.setState({
         playerHand: playerHand.addCard(deck)
       })
       if (playerHand.busted) {
         console.log("bust");
         this.setState({
         hitDisabled: true,
         stayDisabled: true,
         playerDone: true,
         dealerDone: true,
         message: "Player busted! Dealer Wins!"
       })
       }
       break;
     case "dealer":
        this.setState({
          dealerHand: dealerHand.addCard(deck)
        })
        if (dealerHand.busted) {
          this.setState({
            dealerDone: true,
            message: "Dealer Busted! Player Wins!"
          })
        }
        else if (dealerHand.total >= 17 && dealerHand.total <= 21) {
          this.setState({
            dealerDone: true,
            restartDisabled: false,
          })
        }
        break;
     case "aiPlayer":
        this.setState({
          aiPlayerHand: aiPlayerHand.addCard(aiDeck)
        })
        if (aiPlayerHand.busted) {
          this.setState({
            aiPlayerDone: true,
            aiMessage: "AI Busted! AI Dealer Wins!"
          })
        }
        break;
     case "aiDealer":
       this.setState({
         aiDealerHand: aiDealerHand.addCard(aiDeck)
       })
       if (aiDealerHand.busted) {
         this.setState({
           dealerDone: true,
           message: "Dealer Busted! Player Wins!"
         })
       }
       else if (aiDealerHand.total >= 17 && aiDealerHand.total <= 21) {
         this.setState({
           dealerDone: true,
           restartDisabled: false,
         })
       }
        break;
     default:
        console.log("Error: Player type not recognized")
        break;
      }

  }

  async loadTheModel () {
    var model
    try {
      model = await tf.loadLayersModel('model.json');
      return model
    }
    catch (err) {
      console.log(err);
      console.log("failed load model");
    }
  }

  startGame() {
    this.setState({
      playerHand: [],
      dealerHand: [],
      playerDone: false,
      dealerDone: false,
      hitDisabled: false,
      stayDisabled: false,
      restartDisabled: true,
      message: "hit or stay",
    })
    buildDeck()
    dealCards()
    if (this.checkForBlackjack(dealerHand, playerHand) === true) {
      this.setState({
        hitDisabled: true,
        stayDisabled: true,
        restartDisabled: false
      })
    }
    console.log('start')
  }

  stay(hand) {
    switch (hand.owner) {
      case "player":
        this.setState({
          hitDisabled: true,
          stayDisabled: true,
          playerDone: true,
        })
        this.getDealerMoves(dealerHand, playerHand)
      case "aiPlayer":
        console.log("aiPlayer stayed")
      default:
        console.log("unknown player stayed")
    }
  }

  render() {
    return (
      <Box style={gameStyle}>
        <Box style={playerSideStyle}>
          <Box>
            {this.state.message}
          </Box>
          <Box style={dealerStyle}>
            Dealer
            {this.getCards(dealerHand, "dealer")}
          </Box>
        <Box style={playerStyle}>
          Player
          {this.getCards(playerHand, "player")}
          <div style={gameButtons}>
            <button disabled={this.state.hitDisabled} onClick={() => this.hit("player")} style={gameButton}>HIT</button>
            <button disabled={this.state.stayDisabled} onClick={() => this.stay(playerHand)} style={gameButton}>STAY</button>
            <button disabled={this.state.restartDisabled} onClick={() => this.startGame()} style={gameButton}>PLAY AGAIN</button>
          </div>
          </Box>
        </Box>
        <Box style={aiSideStyle}>
          <Box>
            {this.state.aiMessage}
          </Box>
          <Box style={dealerStyle}>
            AI Dealer
            {this.getCards(aiDealerHand, "dealer")}
          </Box>
          <Box style={aiStyle}>
            AI Player
            {this.getCards(aiPlayerHand, "aiPlayer")}
          </Box>
        </Box>
      </Box>
    )
  }
}

function buildDeck() {
  deck = []
  for (var i = 0; i < 8; i++) {
    for (var j = 2; j < 12; j++) {
      if (j === 10) {
        deck.push(j)
        deck.push(j)
        deck.push(j)
      }
      deck.push(j)
    }
  }
  shuffle(deck)
  aiDeck = deck
  console.log("building deck")
}

function dealCards() {
  playerHand.cards = [];
  dealerHand.cards = [];
  aiPlayerHand.cards = [];
  aiDealerHand.cards = [];
  for (var i = 0; i < 2; i++) {
    playerHand.addCard(deck)
    dealerHand.addCard(deck)
  }
  aiPlayerHand.getTotalCardValue()
  aiDealerHand.getTotalCardValue()
  aiPlayerHand.cards = playerHand.cards.slice()
  aiDealerHand.cards = dealerHand.cards.slice()
  console.log("dealing cards")
}

function getCard(card, index) {
  return (
    <Image key={index.toString()} style={cardStyle} src={cardValues[card]}/>
  );
}

function shuffle(array) {
  var currentIndex = array.length,  randomIndex;

  // While there remain elements to shuffle...
  while (0 !== currentIndex) {

    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }

  return array;
}

export default BlackjackGame;
